const fs = require('fs-extra');

module.exports = function ({ api, event }) {
    const { senderID, reaction, messageID, threadID } = event;
    const dataPath = "./modules/commands/data/icon.json";
    const check = fs.existsSync(dataPath)
        ? JSON.parse(fs.readFileSync(dataPath, 'utf-8')).find(i => i.threadID === threadID)
        : null;
    const icon = check ? check.icon : null;

    if (senderID === api.getCurrentUserID() && (reaction === icon || reaction === "😠")) {
        return api.unsendMessage(messageID);
    }
};

